import React,{useState, useEffect } from 'react'
import axios from 'axios'
import {Card, Avatar, Row ,Col} from 'antd'
import { Pagination } from 'antd';
import BlogCard from './BlogCard'
import {HeartOutlined,CommentOutlined, DislikeOutlined } from '@ant-design/icons'
const { Meta } = Card;


const BlogList = () => {
    const [allPostsData, setAllPosts] = useState([]);
    const[loading, setLoading] = useState(false)
    const[currentpage, setCurrentPage] =useState(1);
    const onChange = (page) =>{
      setCurrentPage(page)
    }
    useEffect(() => {
        setLoading(true)
        const data = axios.get("http://localhost:8000/bloggers")
        .then((res) =>{
            const data = res.data
            setAllPosts(data)
            setLoading(false)
            console.log("data is",data)

        })
        .catch((err) =>{
            console.log(err)
        })

    }, [allPostsData])
    return (
      <>

        <Row gutter={16,{ xs: 8, sm: 16, md: 24, lg: 32 }} style={{ justifyContent: "space-around",minHeight: "100vh" }}>
        {allPostsData.map((blog) => (
          <Col span={6} key={blog.id} className="mt-10 mb-10">
            <BlogCard id={blog.id} title={blog.title} name={blog.name} description={blog.description} image={blog.image} avatar={blog.author_image} />
          </Col>
        ))}
      </Row>
      <Pagination style={{textAlign:"center"}} current={currentpage} onChange={onChange} total={50} />
      </>

    )
}

export default BlogList
