import React from 'react'
import {Row, Card, Col, Form, Input, Button, message} from 'antd'
import {Link } from 'react-router-dom'
import axios from 'axios'



const layout = {
    labelCol: { span: 5 },
    wrapperCol: { span: 19 },
  };
  const tailLayout = {
    wrapperCol: { span: 16 },
  };

const PostBlog = () => {
    const onFinish = (values) => {
        console.log("Success:", values);
        axios.post("http://localhost:8000/bloggers" )
        .then((res)=>{
         console.log("response is",res)
         message.success(`Your blog has been created on /${res.data.id}`, 10);

        }).catch((err)=>{
          console.log("Error",err)
        })
      };

      const onFinishFailed = (err) => {
        console.log("Failed:", err);
      };
    return (
        <div>
            <Row
        style={{
          display: "flex",
          minHeight: "90vh",
          alignItems: "self-start",
          justifyContent: "center",
        }}
      >
        <Card title="Add Your Blog" style={{ width: "50%", borderRadius:"10px", background:"#b3b3ff",textAlign:"center" }}>
          <Col md={18} lg={18} sm={24}>
            <Form
              {...layout}
              name="basic"
              initialValues={{ remember: true }}
              onFinish={onFinish}
              onFinishFailed={onFinishFailed}
            >
              <Form.Item
                label="Author of Blog "
                name="name"
                rules={[{ required: true, message: "Please input your name!" }]}
              >
                <Input />
              </Form.Item>

              <Form.Item
                label="Title of blog"
                name="title"
                rules={[
                  { required: true, message: "Please input blog title!" },
                ]}
              >
                <Input />
              </Form.Item>
              <Form.Item
                label="Add Description"
                name="description"
                rules={[
                  { required: true, message: "Please input blog description!" },
                ]}
              >
                <Input.TextArea />
              </Form.Item>

              <Form.Item
                label="Image Link"
                name="image"
                rules={[
                  { required: true, message: "Please input blog title!" },
                ]}
              >
                <Input />
              </Form.Item>

              <Form.Item style={{ textAlign: "center" }} {...tailLayout}>
                <Button type="primary "  htmlType="submit">
                  Post Blog
                </Button>
                {/* {
                  showblog && <Link to={"/blog/" + showblog}> 
                  <Button style={{marginLeft: "15px"}} type="sucess ">
                  View
                </Button>
                  </Link>
                } */}
              </Form.Item>
            </Form>
          </Col>
        </Card>
      </Row>

        </div>
    )
}

export default PostBlog
