import React ,{useEffect,useState}from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'
import {List, Row, Col,Image,Title, Typography} from 'antd'

const Home = () => {
    const[blogs,setBlogs]= useState([])
    useEffect(() => {
        console.log("inside useeffect");

          axios.get("http://localhost:8000/bloggers")
          .then((res) => {
            console.log(res.data);
            setBlogs(res.data);
            localStorage.setItem("blogs",JSON.stringify(res.data))

          })
          .catch((err) => {
            console.log("errrrrrr+++",err);

          });
      }, []);
    return (
        <div>
            <Row style={{ marginTop: "15px" }} className="homeRow">

                <Col md={16} sm={24} className="blogList">
                    <h2 style={{borderBottom: "0",marginTop: "4px", textAlign:"center", padding: "10px 0", background: "rgb(0, 21, 41)",color:"white"}}>Some Featured Blogs</h2>

                    <List

                    style={{ padding: "15px" }}
                    dataSource={blogs}
                    pagination={{
                        onChange: page => {
                        console.log(page);
                        },
                        pageSize: 6,
                    }}
                    renderItem={(item) => (
                        <List.Item 
                        style={{marginBottom: "10px"}}
                        >
                        <List.Item.Meta
                            style={{ textAlign: "start",padding: "0 26px" }}
                            // avatar={<UserOutlined />}
                            title={
                            <div className="listRow">
                                <Link to={"/blogdetails/"+item.id}><Typography> {item.title}</Typography></Link>
                                <p style={{ marginBottom: 0 }}>{item.date}</p>
                            </div>
                            }
                            description={
                            <div >
                                <Image
                                width="300px"
                                height="300px"
                                className="blogImage"
                                src={item.image}
                                />
                                <p>{item.description.substring(0,200)} <Link to={"/blog/"+item.id}>Read More</Link></p>
                            </div>
                            }
                        />
                        </List.Item>
                    )}
                    />
                </Col>
            </Row>
        </div>
    )
}

export default Home
