import React,{useState, useEffect } from 'react'
import axios from 'axios'
import {Card, Avatar, Row ,Col} from 'antd'
import BlogCard from './BlogCard'
import {HeartOutlined,CommentOutlined, DislikeOutlined } from '@ant-design/icons'
const { Meta } = Card;


const BlogList = () => {
    const [allPostsData, setAllPosts] = useState([]);
    const ShowImage = () => {
        return props.image.map((img,index) =>{
            <Image 
                image = {props.image}
                index = {index}
                key = {index}
            />
        })
    }
    useEffect(() => {
        const data = axios.get("http://localhost:8000/bloggers")
        .then((res) =>{
            const data = res.data
            setAllPosts(data)
            console.log("data is",data)

        })
        .catch((err) =>{
            console.log(err)
        })

    }, [])
    return (
        <Row style={{ justifyContent: "space-around",minHeight: "100vh" }}>
        {allPostsData.map((blog) => (
          <Col md={7} key={allPostsData.id} className="mt-10 mb-10">
            <BlogCard title={blog.title} name={blog.name} description={blog.description} image={ShowImage} />
          </Col>
        ))}
      </Row>

    )
}

export default BlogList
